import React, {useState} from 'react';
import {useAppDispatch} from "../../redux/hooks";
import {removeSeminar} from "../../redux/seminarsSlice";
import styles from './SeminarControl.module.scss';
import ConfirmDeleteModal from "../common/ConfirmDeleteModal/ConfirmDeleteModal";
import {IdType} from "../../types";

type TodoControlType = {
    setEditFormIsOpen: (isEditing: boolean) => void,
    id: IdType
}

const SeminarControl: React.FC<TodoControlType> = (props) => {

    const [confirmDelete, setConfirmDelete] = useState(false)

    const dispatch = useAppDispatch()

    const deleteItem = () => {
        dispatch(removeSeminar(props.id))
        setConfirmDelete(false)
    }
    const editItem = () => {
        props.setEditFormIsOpen(true)
    }

    return (
        <div className={styles.control}>
            <ConfirmDeleteModal deleteItem={deleteItem}
                                confirmDelete={confirmDelete}
                                setConfirmDelete={setConfirmDelete}
            />
            <div className={styles.control__item} onClick={editItem}>
                <svg className="w-6 h-6 text-gray-800 dark:text-white" aria-hidden="true"
                     xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="none" viewBox="0 0 24 24">
                    <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2"
                          d="m14.304 4.844 2.852 2.852M7 7H4a1 1 0 0 0-1 1v10a1 1 0 0 0 1 1h11a1 1 0 0 0 1-1v-4.5m2.409-9.91a2.017 2.017 0 0 1 0 2.853l-6.844 6.844L8 14l.713-3.565 6.844-6.844a2.015 2.015 0 0 1 2.852 0Z"/>
                </svg>
            </div>
            <div className={styles.control__item} onClick={() => setConfirmDelete(true)}>
                <svg className="w-6 h-6 text-gray-800 dark:text-white" aria-hidden="true"
                     xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="none" viewBox="0 0 24 24">
                    <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2"
                          d="M5 7h14m-9 3v8m4-8v8M10 3h4a1 1 0 0 1 1 1v3H9V4a1 1 0 0 1 1-1ZM6 7h12v13a1 1 0 0 1-1 1H7a1 1 0 0 1-1-1V7Z"/>
                </svg>
            </div>
        </div>
    );
};

export default SeminarControl;
